INSERT INTO `clinimetrix_registry` VALUES ('cmdunar2a0001y9fbprfre47v','bdi-13-1.0','BDI-13','Inventario de Depresión de Beck (Versión Abreviada)','Depresión','Evaluación de síntomas depresivos','Versión abreviada del inventario original de Beck desarrollada en 1974 para evaluación rápida y eficiente de la intensidad de los síntomas depresivos en adultos y adolescentes','1.0','es','Aaron T. Beck, William Y. Rial, Karl Rickels','1974','both',5,'\"{\\\"ageGroups\\\":[\\\"adolescentes\\\",\\\"adultos\\\"],\\\"demographics\\\":\\\"Población general, estudiantes universitarios, pacientes clínicos\\\",\\\"clinicalConditions\\\":[\\\"Depresión mayor\\\",\\\"Trastornos del estado de ánimo\\\",\\\"Evaluación de severidad depresiva\\\"]}\"',13,0,39,'\"{\\\"reliability\\\":{\\\"cronbachAlpha\\\":\\\"0.85-0.92\\\",\\\"testRetest\\\":\\\"0.75-0.85 (1-4 semanas)\\\",\\\"interRater\\\":\\\"0.95-0.98\\\"},\\\"validity\\\":{\\\"construct\\\":\\\"Estructura bifactorial confirmada: Factor Cognitivo-Afectivo (9 ítems) y Factor Somático (4 ítems). Análisis factorial confirma modelo teórico propuesto.\\\",\\\"criterion\\\":\\\"Correlación alta con BDI completo (r = 0.96-0.98). Correlación moderada-alta con otras escalas de depresión (r = 0.70-0.85).\\\",\\\"discriminant\\\":\\\"Correlaciones moderadas con escalas de ansiedad (r = 0.45-0.60), indicando especificidad adecuada para síntomas depresivos.\\\",\\\"sensitivity\\\":\\\"75-85% para detección de depresión mayor\\\",\\\"specificity\\\":\\\"85-95% para población general\\\",\\\"positivePredicativeValue\\\":\\\"70-80% dependiendo de prevalencia poblacional\\\",\\\"negativePredicativeValue\\\":\\\"90-95% en poblaciones de baja prevalencia\\\"},\\\"factorStructure\\\":\\\"Modelo bifactorial robusto: Factor 1 (Cognitivo-Afectivo): ítems 1-9, evalúa tríada cognitiva negativa y síntomas afectivos centrales; Factor 2 (Somático): ítems 10-13, evalúa manifestaciones físicas y neurovegetativas.\\\",\\\"minimumDetectableChange\\\":\\\"3-4 puntos en la escala total para cambio clínicamente significativo\\\"}\"','\"{\\\"diagnosticAccuracy\\\":\\\"Área bajo la curva ROC de 0.85-0.92 para detección de episodio depresivo mayor\\\",\\\"clinicalTrials\\\":\\\"Utilizado en más de 500 estudios clínicos como medida de resultado primaria o secundaria\\\",\\\"treatmentOutcomes\\\":\\\"Correlación moderada-alta (r = 0.65-0.80) con mejora clínica global evaluada por clínicos\\\"}\"',1,0,'\"[\\\"Depresión\\\",\\\"Evaluación de síntomas depresivos\\\"]\"','2025-08-02 19:28:18.561',1,'2025-08-02 19:28:18.561','2025-08-02 19:28:18.561'),('cmdunar2m0003y9fbw1lvqvek','bdi-21-1.0','BDI-21','Inventario de Depresión de Beck (Versión Original)','Depresión','Evaluación integral de síntomas depresivos','Instrumento original desarrollado por Beck en 1961 para evaluación integral y detallada de la intensidad de síntomas depresivos en adultos y adolescentes, considerando múltiples dominios sintomáticos','1.0','es','Aaron T. Beck, C. H. Ward, M. Mendelson, J. Mock, J. Erbaugh','1961','both',15,'\"{\\\"ageGroups\\\":[\\\"adolescentes\\\",\\\"adultos\\\"],\\\"demographics\\\":\\\"Población general, estudiantes universitarios, pacientes clínicos, población médica\\\",\\\"clinicalConditions\\\":[\\\"Depresión mayor\\\",\\\"Trastornos del estado de ánimo\\\",\\\"Trastorno bipolar\\\",\\\"Evaluación de severidad depresiva\\\",\\\"Monitoreo terapéutico\\\"]}\"',21,0,63,'\"{\\\"reliability\\\":{\\\"cronbachAlpha\\\":\\\"0.87-0.92 (población mexicana), 0.86-0.93 (población española)\\\",\\\"testRetest\\\":\\\"0.72 (1 mes), 0.60-0.69 (3 meses) en población española\\\",\\\"interRater\\\":\\\"0.95-0.98 cuando administrado por diferentes evaluadores\\\"},\\\"validity\\\":{\\\"construct\\\":\\\"Estructura tetrafactorial robusta confirmada: Factor Cognitivo (5 ítems), Factor Afectivo (8 ítems), Factor Somático (3 ítems), Factor Vegetativo (5 ítems). Validez factorial confirmada en múltiples poblaciones.\\\",\\\"criterion\\\":\\\"Correlación alta con criterios diagnósticos DSM-5 para depresión mayor (r = 0.75-0.85). Correlación elevada con evaluación clínica estructurada (r = 0.70-0.80).\\\",\\\"discriminant\\\":\\\"Correlación moderada con escalas de ansiedad (r = 0.55-0.65), confirmando especificidad para síntomas depresivos. Diferenciación adecuada entre poblaciones clínicas y no clínicas.\\\",\\\"sensitivity\\\":\\\"80-90% para detección de episodio depresivo mayor\\\",\\\"specificity\\\":\\\"85-93% en población general\\\",\\\"positivePredicativeValue\\\":\\\"75-85% dependiendo de prevalencia poblacional\\\",\\\"negativePredicativeValue\\\":\\\"90-95% en poblaciones comunitarias\\\"},\\\"factorStructure\\\":\\\"Modelo tetrafactorial validado: 1) Síntomas Cognitivos (ítems 2,3,6,8,13): tríada cognitiva negativa y distorsiones; 2) Síntomas Afectivos (ítems 1,4,5,7,9,10,11,12): estado de ánimo y regulación emocional; 3) Síntomas Somáticos (ítems 14,17,20): manifestaciones físicas conscientes; 4) Síntomas Vegetativos (ítems 15,16,18,19,21): funciones neurovegetativas automáticas.\\\",\\\"minimumDetectableChange\\\":\\\"5-6 puntos en escala total para cambio clínicamente significativo. 2-3 puntos por subescala para cambios específicos por dominio.\\\"}\"','\"{\\\"diagnosticAccuracy\\\":\\\"Área bajo la curva ROC de 0.88-0.94 para detección de episodio depresivo mayor según criterios DSM-5\\\",\\\"clinicalTrials\\\":\\\"Utilizado como medida primaria en más de 1,000 ensayos clínicos internacionales. Instrumento de referencia en investigación de eficacia antidepresiva\\\",\\\"treatmentOutcomes\\\":\\\"Correlación alta (r = 0.75-0.85) con mejora clínica global. Sensibilidad al cambio demostrada en múltiples modalidades terapéuticas (farmacológica, psicoterapéutica, neuromodulación)\\\"}\"',1,1,'\"[\\\"Depresión\\\",\\\"Evaluación integral de síntomas depresivos\\\"]\"','2025-08-02 19:28:18.574',1,'2025-08-02 19:28:18.574','2025-08-02 19:28:18.574'),('cmdunar2s0005y9fb2byts5a5','gadi-1.0','GADI','Inventario de Ansiedad Generalizada','Ansiedad','Trastorno de Ansiedad Generalizada','Instrumento autoaplicado diseñado para evaluar la presencia, intensidad y severidad de los síntomas del Trastorno de Ansiedad Generalizada según criterios diagnósticos actuales. Evalúa síntomas cognitivos, somáticos y alteraciones del sueño relacionados con ansiedad generalizada.','1.0','es','Spilios V. Argyropoulos, Tamsin S. Wright, Sam W. Forshall, David J. Nutt','2007','both',8,'\"{\\\"ageGroups\\\":[\\\"adultos\\\",\\\"adulto mayor\\\"],\\\"demographics\\\":\\\"Población general adulta, estudiantes universitarios, pacientes con sospecha de TAG\\\",\\\"clinicalConditions\\\":[\\\"Trastorno de Ansiedad Generalizada\\\",\\\"Trastornos de ansiedad\\\",\\\"Síntomas ansiosos\\\"]}\"',22,0,88,'\"{\\\"reliability\\\":{\\\"cronbachAlpha\\\":\\\".928\\\",\\\"testRetest\\\":\\\".85 (4 semanas)\\\",\\\"interRater\\\":\\\"No aplicable (autoinforme)\\\"},\\\"validity\\\":{\\\"construct\\\":\\\"Estructura factorial de 3 factores confirmada mediante AFC en múltiples estudios: síntomas cognitivos, somáticos y alteraciones del sueño, explicando aproximadamente 60% de la varianza total\\\",\\\"criterion\\\":\\\"Correlaciones significativas con PSWQ (r=.65), BAI (r=.72) y HAM-A. Discrimina efectivamente entre pacientes con TAG y controles sanos\\\",\\\"discriminant\\\":\\\"El factor cognitivo distingue TAG de otros trastornos de ansiedad y depresión. Baja correlación con medidas de personalidad no relacionadas\\\",\\\"sensitivity\\\":\\\"85-90% (punto de corte ≥23)\\\",\\\"specificity\\\":\\\"78-82% (punto de corte ≥23)\\\",\\\"positivePredicativeValue\\\":\\\"73-80%\\\",\\\"negativePredicativeValue\\\":\\\"88-92%\\\"},\\\"factorStructure\\\":\\\"Estructura trifactorial estable: Factor 1 (Cognitivo): 9 ítems relacionados con preocupación y control de ansiedad; Factor 2 (Somático): 11 ítems de síntomas físicos e irritabilidad; Factor 3 (Sueño): 2 ítems de alteraciones del sueño\\\",\\\"minimumDetectableChange\\\":\\\"Cambio mínimo clínicamente significativo estimado en 8-12 puntos en puntuación total\\\"}\"','\"{\\\"diagnosticAccuracy\\\":\\\"Precisión diagnóstica del 83% para identificar TAG cuando se compara con evaluación clínica estructurada\\\",\\\"clinicalTrials\\\":\\\"Utilizado en estudios de eficacia de tratamientos farmacológicos y psicoterapéuticos para TAG. Sensible al cambio terapéutico\\\",\\\"treatmentOutcomes\\\":\\\"Muestra sensibilidad para detectar mejoría clínica en estudios de intervención. Correlación moderada-alta con medidas de funcionamiento global\\\"}\"',1,1,'\"[\\\"Ansiedad\\\",\\\"Trastorno de Ansiedad Generalizada\\\"]\"','2025-08-02 19:28:18.579',1,'2025-08-02 19:28:18.579','2025-08-02 19:28:18.579'),('cmdunar2y0007y9fbpb4hap23','gds-15-1.0','GDS-15','Escala de Depresión Geriátrica (15 ítems)','Depresión','Depresión Geriátrica','Versión abreviada de la GDS que mantiene alta sensibilidad y especificidad con tiempo de aplicación reducido. Los ítems fueron seleccionados por su mayor correlación con el diagnóstico clínico de depresión.','1.0','es','Jerome I. Sheikh, Jerome A. Yesavage','1986','both',5,'\"{\\\"ageGroups\\\":[\\\"adultos mayores\\\"],\\\"demographics\\\":\\\"Adultos de 65 años o más\\\",\\\"clinicalConditions\\\":[\\\"Trastornos del estado de ánimo en población geriátrica\\\",\\\"Tamizaje rápido de depresión\\\",\\\"Seguimiento clínico\\\"]}\"',15,0,15,'\"{\\\"reliability\\\":{\\\"cronbachAlpha\\\":\\\"0.715-0.84\\\",\\\"testRetest\\\":\\\"0.85-0.89\\\",\\\"interRater\\\":\\\"0.86-0.91\\\"},\\\"validity\\\":{\\\"construct\\\":\\\"Estructura factorial bifactorial: factor general de depresión y factor específico de bienestar subjetivo. Correlación r = 0.84 con GDS-30\\\",\\\"criterion\\\":\\\"Validez concurrente excelente con criterios DSM-IV/DSM-5 para depresión mayor. Correlación con evaluación clínica: r = 0.73-0.89\\\",\\\"discriminant\\\":\\\"Capacidad excelente para discriminar entre depresión clínica y no clínica en población geriátrica\\\",\\\"sensitivity\\\":\\\"82-94%\\\",\\\"specificity\\\":\\\"81-98%\\\",\\\"positivePredicativeValue\\\":\\\"78-86%\\\",\\\"negativePredicativeValue\\\":\\\"94-97%\\\"},\\\"factorStructure\\\":\\\"Estructura bidimensional: factor afectivo-cognitivo principal y factor de bienestar secundario, explicando 45-52% de la varianza total\\\",\\\"minimumDetectableChange\\\":\\\"Cambio de 3-4 puntos considerado clínicamente significativo\\\"}\"','\"{\\\"diagnosticAccuracy\\\":\\\"AUC = 0.89-0.95 en estudios de validación multicéntricos\\\",\\\"clinicalTrials\\\":\\\"Utilizada como medida primaria o secundaria en >150 ensayos clínicos de depresión geriátrica\\\",\\\"treatmentOutcomes\\\":\\\"Sensible al cambio con tratamiento. Correlación r = 0.68 con mejoría clínica global medida por CGI\\\"}\"',1,0,'\"[\\\"Depresión\\\",\\\"Depresión Geriátrica\\\"]\"','2025-08-02 19:28:18.586',1,'2025-08-02 19:28:18.586','2025-08-02 19:28:18.586'),('cmdunar330009y9fb2kg1kbqg','gds-30-1.0','GDS-30','Escala de Depresión Geriátrica (30 ítems)','Depresión','Depresión Geriátrica','Escala específicamente diseñada para la detección de síntomas depresivos en población adulta mayor, eliminando síntomas somáticos que pueden confundir el diagnóstico en esta población.','1.0','es','Jerome A. Yesavage, T.L. Brink, Terence L. Rose, Owen Lum, Virginia Huang, Michael Adey, Von Otto Leirer','1982-1983','both',10,'\"{\\\"ageGroups\\\":[\\\"adultos mayores\\\"],\\\"demographics\\\":\\\"Adultos de 65 años o más\\\",\\\"clinicalConditions\\\":[\\\"Trastornos del estado de ánimo en población geriátrica\\\",\\\"Tamizaje de depresión\\\",\\\"Evaluación integral geriátrica\\\"]}\"',30,0,30,'\"{\\\"reliability\\\":{\\\"cronbachAlpha\\\":\\\"0.82-0.94\\\",\\\"testRetest\\\":\\\"0.80-0.85\\\",\\\"interRater\\\":\\\"0.83-0.89\\\"},\\\"validity\\\":{\\\"construct\\\":\\\"Validez de constructo confirmada mediante análisis factorial que identifica factores de estado de ánimo depresivo, ansiedad, desesperanza y bienestar subjetivo\\\",\\\"criterion\\\":\\\"Validez concurrente alta con criterios DSM-IV para trastorno depresivo mayor (r = 0.84). Validez predictiva demostrada para respuesta al tratamiento\\\",\\\"discriminant\\\":\\\"Capacidad excelente para discriminar entre población deprimida y no deprimida. Sensibilidad: 92%, Especificidad: 89%\\\",\\\"sensitivity\\\":\\\"84-97%\\\",\\\"specificity\\\":\\\"63-95%\\\",\\\"positivePredicativeValue\\\":\\\"50-85%\\\",\\\"negativePredicativeValue\\\":\\\"90-97%\\\"},\\\"factorStructure\\\":\\\"Estructura factorial de 6 componentes: estado de ánimo depresivo-ansioso, bienestar y optimismo, preocupaciones y pesimismo, funcionamiento cognitivo, pérdida de interés y anhedonia, actividad social\\\",\\\"minimumDetectableChange\\\":\\\"Cambio de 4-5 puntos considerado clínicamente significativo\\\"}\"','\"{\\\"diagnosticAccuracy\\\":\\\"AUC = 0.85-0.92 en múltiples estudios de validación\\\",\\\"clinicalTrials\\\":\\\"Utilizada como medida de resultado en >200 ensayos clínicos de tratamiento de depresión geriátrica\\\",\\\"treatmentOutcomes\\\":\\\"Correlación r = 0.72 con mejoría clínica global. Sensible al cambio con tratamiento antidepresivo y psicoterapéutico\\\"}\"',1,0,'\"[\\\"Depresión\\\",\\\"Depresión Geriátrica\\\"]\"','2025-08-02 19:28:18.591',1,'2025-08-02 19:28:18.591','2025-08-02 19:28:18.591'),('cmdunar37000by9fbosq2erp0','gds-5-1.0','GDS-5','Escala de Depresión Geriátrica (5 ítems)','Depresión','Depresión Geriátrica','Versión ultra-breve de la GDS desarrollada para tamizaje rápido de depresión geriátrica. Los 5 ítems fueron seleccionados por su máxima correlación con el diagnóstico clínico, manteniendo alta sensibilidad con tiempo mínimo de aplicación.','1.0','es','M. Teresa Hoyl, Catherine A. Alessi, John O. Harker, Karen R. Josephson','1997','both',2,'\"{\\\"ageGroups\\\":[\\\"adultos mayores\\\"],\\\"demographics\\\":\\\"Adultos de 65 años o más\\\",\\\"clinicalConditions\\\":[\\\"Tamizaje rápido de depresión\\\",\\\"Evaluaciones de urgencia\\\",\\\"Población frágil\\\",\\\"Seguimiento frecuente\\\"]}\"',5,0,5,'\"{\\\"reliability\\\":{\\\"cronbachAlpha\\\":\\\"0.495-0.72\\\",\\\"testRetest\\\":\\\"0.84-0.89\\\",\\\"interRater\\\":\\\"0.88-0.92\\\"},\\\"validity\\\":{\\\"construct\\\":\\\"Estructura unifactorial que explica 48.1% de la varianza. Correlación r = 0.92 con GDS-15 y r = 0.89 con GDS-30\\\",\\\"criterion\\\":\\\"Correlación r = 0.726 con criterios DSM-5 para depresión mayor. Validez concurrente excelente con evaluación clínica (kappa = 0.81)\\\",\\\"discriminant\\\":\\\"Capacidad excelente para identificar casos positivos que requieren evaluación adicional\\\",\\\"sensitivity\\\":\\\"86-97%\\\",\\\"specificity\\\":\\\"68-90%\\\",\\\"positivePredicativeValue\\\":\\\"54-88%\\\",\\\"negativePredicativeValue\\\":\\\"94-98%\\\"},\\\"factorStructure\\\":\\\"Estructura unidimensional que representa un factor general de bienestar emocional/depresión con cargas factoriales superiores a 0.60 en todos los ítems\\\",\\\"minimumDetectableChange\\\":\\\"Cambio de 1-2 puntos considerado clínicamente significativo dado el rango limitado\\\"}\"','\"{\\\"diagnosticAccuracy\\\":\\\"AUC = 0.85-0.92. Rendimiento óptimo como instrumento de tamizaje con alta sensibilidad\\\",\\\"clinicalTrials\\\":\\\"Utilizada en >30 estudios como herramienta de tamizaje inicial y en programas de detección poblacional\\\",\\\"treatmentOutcomes\\\":\\\"Útil para monitoreo de respuesta al tratamiento aunque menos sensible al cambio que versiones más largas\\\"}\"',1,0,'\"[\\\"Depresión\\\",\\\"Depresión Geriátrica\\\"]\"','2025-08-02 19:28:18.595',1,'2025-08-02 19:28:18.595','2025-08-02 19:28:18.595'),('cmdunar3f000dy9fb332w0dn9','hdrs-17-v1.0','HDRS-17','Escala de Hamilton para la Depresión','Depresión','Evaluación de severidad','Escala heteroaplicada de 17 ítems para evaluar la severidad de síntomas depresivos en adultos con diagnóstico confirmado o sospecha fundada de trastorno depresivo mayor. Considerada el estándar de oro para evaluación clínica de la depresión y monitoreo de respuesta terapéutica.','1.0','es','Hamilton, Max','1960','clinician_administered',20,'\"{\\\"ageGroups\\\":[\\\"adultos\\\"],\\\"demographics\\\":\\\"Adultos ≥18 años con diagnóstico confirmado o sospecha fundada de trastorno depresivo mayor\\\",\\\"clinicalConditions\\\":[\\\"Trastorno depresivo mayor\\\",\\\"Episodio depresivo\\\",\\\"Trastorno bipolar en fase depresiva\\\"]}\"',17,0,52,'\"{\\\"reliability\\\":{\\\"cronbachAlpha\\\":\\\"0.806\\\",\\\"testRetest\\\":\\\"0.81\\\",\\\"interRater\\\":\\\"0.94\\\"},\\\"validity\\\":{\\\"construct\\\":\\\"Validez de constructo bien establecida con análisis factorial confirmatorio que identifica factores de melancolía, ansiedad y sueño\\\",\\\"criterion\\\":\\\"Validez concurrente demostrada con otras escalas de depresión (r=0.73-0.84 con BDI, r=0.71-0.79 con MADRS)\\\",\\\"discriminant\\\":\\\"Capacidad discriminante excelente entre grupos con y sin depresión (AUC=0.94)\\\",\\\"sensitivity\\\":\\\"0.95\\\",\\\"specificity\\\":\\\"0.94\\\",\\\"positivePredicativeValue\\\":\\\"89%\\\",\\\"negativePredicativeValue\\\":\\\"97%\\\"},\\\"factorStructure\\\":\\\"Estructura de 3 factores confirmada: Factor 1 (Melancolía): ítems 1,2,7,8,10,13; Factor 2 (Ansiedad): ítems 9,10,11; Factor 3 (Sueño): ítems 4,5,6\\\",\\\"minimumDetectableChange\\\":\\\"Reducción de 3 puntos o 25% de la puntuación basal se considera clínicamente significativa\\\"}\"','\"{\\\"diagnosticAccuracy\\\":\\\"Precisión diagnóstica del 92% para detectar episodio depresivo mayor con punto de corte ≥14\\\",\\\"clinicalTrials\\\":\\\"Utilizada en más de 3,000 ensayos clínicos como medida primaria de eficacia antidepresiva\\\",\\\"treatmentOutcomes\\\":\\\"Reducción ≥50% correlaciona con respuesta clínica; puntuación ≤7 indica remisión clínica\\\"}\"',1,0,'\"[\\\"Depresión\\\",\\\"Evaluación de severidad\\\"]\"','2025-08-02 19:28:18.603',1,'2025-08-02 19:28:18.603','2025-08-02 19:28:18.603'),('cmdunar3j000fy9fbq6n0cy3r','madrs-1.0','MADRS','Escala de Depresión de Montgomery-Asberg','Depresión','Episodios depresivos mayores','Escala clínica de 10 ítems diseñada para evaluar la severidad de episodios depresivos y medir cambios en la sintomatología depresiva durante el tratamiento, con especial sensibilidad a los efectos terapéuticos','1.0','es','Stuart Montgomery, Marie Åsberg','1979','clinician_administered',15,'\"{\\\"ageGroups\\\":[\\\"adultos\\\"],\\\"demographics\\\":\\\"Pacientes con trastornos del estado de ánimo diagnosticados\\\",\\\"clinicalConditions\\\":[\\\"Episodio depresivo mayor\\\",\\\"Trastorno bipolar\\\",\\\"Trastornos del estado de ánimo\\\"]}\"',10,0,60,'\"{\\\"reliability\\\":{\\\"cronbachAlpha\\\":\\\"0.88-0.95\\\",\\\"testRetest\\\":\\\"0.94\\\",\\\"interRater\\\":\\\"0.89-0.98\\\"},\\\"validity\\\":{\\\"construct\\\":\\\"Estructura factorial confirmada con modelo unidimensional de depresión. Correlación alta con HAM-D (r=0.87-0.94)\\\",\\\"criterion\\\":\\\"Validez concurrente excelente con Clinical Global Impression (r>0.85). Correlación significativa con escalas de calidad de vida\\\",\\\"discriminant\\\":\\\"Capacidad superior para discriminar entre diferentes niveles de severidad depresiva\\\",\\\"sensitivity\\\":\\\"90-95%\\\",\\\"specificity\\\":\\\"85-90%\\\",\\\"positivePredicativeValue\\\":\\\"85-92%\\\",\\\"negativePredicativeValue\\\":\\\"90-95%\\\"},\\\"factorStructure\\\":\\\"Estructura unidimensional confirmada. Todos los ítems cargan significativamente en factor general de depresión\\\",\\\"minimumDetectableChange\\\":\\\"Reducción de 6 puntos indica cambio clínicamente significativo; 12 puntos indica cambio sustancial\\\"}\"','\"{\\\"diagnosticAccuracy\\\":\\\"Sensibilidad 90-95% para detectar episodio depresivo mayor moderado-severo\\\",\\\"clinicalTrials\\\":\\\"Escala estándar en >1000 ensayos clínicos de antidepresivos. Aprobada por FDA para estudios de eficacia\\\",\\\"treatmentOutcomes\\\":\\\"Correlación significativa con respuesta al tratamiento. Predictor de remisión sintomática\\\"}\"',1,1,'\"[\\\"Depresión\\\",\\\"Episodios depresivos mayores\\\"]\"','2025-08-02 19:28:18.607',1,'2025-08-02 19:28:18.607','2025-08-02 19:28:18.607'),('cmdunar3t000hy9fbq2uebvjs','mos-sleep-1.0','MOS-Sleep','Escala de Sueño del Estudio de Resultados Médicos','Trastornos del Sueño','Calidad y Trastornos del Sueño','Instrumento de autoinforme de 12 ítems que evalúa múltiples dimensiones del sueño durante las últimas 4 semanas, incluyendo trastornos del sueño, adequación, somnolencia diurna, ronquidos y síntomas respiratorios.','1.0','es','Ron D. Hays, Anita L. Stewart','1992','both',5,'\"{\\\"ageGroups\\\":[\\\"adultos\\\",\\\"adulto mayor\\\"],\\\"demographics\\\":\\\"Población general y clínica adulta\\\",\\\"clinicalConditions\\\":[\\\"Trastornos del sueño\\\",\\\"Insomnio\\\",\\\"Apnea del sueño\\\",\\\"Enfermedades crónicas\\\",\\\"Dolor crónico\\\",\\\"Condiciones médicas que afectan el sueño\\\"]}\"',12,0,100,'\"{\\\"reliability\\\":{\\\"cronbachAlpha\\\":\\\"0.70-0.90 para dominios multítem e índices globales\\\",\\\"testRetest\\\":\\\"Correlaciones sustanciales a casi perfectas para puntuación total\\\",\\\"interRater\\\":\\\"No aplica - instrumento de autoinforme\\\"},\\\"validity\\\":{\\\"construct\\\":\\\"Estructura factorial coherente de 4-6 factores con varianza explicada del 70%. Correlaciones ítem-escala >0.40 para la mayoría de ítems.\\\",\\\"criterion\\\":\\\"Correlaciones significativas con Pittsburgh Sleep Quality Index (r>0.50), SF-36 componentes físico y mental (r=0.28-0.53)\\\",\\\"discriminant\\\":\\\"Discrimina significativamente entre buenos y malos durmientes, pacientes con y sin condiciones médicas que afectan el sueño\\\",\\\"sensitivity\\\":\\\"94% para detectar trastornos del sueño clínicamente significativos\\\",\\\"specificity\\\":\\\"77-94% según población y punto de corte utilizado\\\",\\\"positivePredicativeValue\\\":\\\"Depende de prevalencia 