INSERT INTO `clinimetrix_templates` VALUES ('bdi-13-1.0','{\"scoring\": {\"method\": \"sum\", \"subscales\": [{\"id\": \"cognitivo-afectivo\", \"name\": \"Factor Cognitivo-Afectivo\", \"items\": [1, 2, 3, 4, 5, 6, 7, 8, 9], \"scoreRange\": {\"max\": 27, \"min\": 0}}, {\"id\": \"somatico\", \"name\": \"Factor Somático\", \"items\": [10, 11, 12, 13], \"scoreRange\": {\"max\": 12, \"min\": 0}}], \"scoreRange\": {\"max\": 39, \"min\": 0}, \"reversedItems\": [], \"specialScoring\": {\"notes\": \"Puntuación uniforme de 0-3 puntos por ítem. Suma simple de todos los ítems para puntuación total.\"}}, \"metadata\": {\"id\": \"bdi-13-1.0\", \"name\": \"Inventario de Depresión de Beck (Versión Abreviada)\", \"year\": \"1974\", \"authors\": [\"Aaron T. Beck\", \"William Y. Rial\", \"Karl Rickels\"], \"version\": \"1.0\", \"category\": \"Depresión\", \"helpText\": {\"general\": \"El BDI-13 es una versión abreviada que mantiene las propiedades psicométricas del inventario original. Evalúa la intensidad de síntomas depresivos cognitivos, afectivos y somáticos durante el estado actual del evaluado. Es especialmente útil para screening rápido y monitoreo de cambios terapéuticos.\", \"instructions\": {\"patient\": \"Lea atentamente cada grupo de afirmaciones. Seleccione UNA afirmación de cada grupo que mejor describa cómo se ha sentido ACTUALMENTE. Si varias afirmaciones le parecen aplicables, seleccione la que indique el mayor grado de intensidad. Es importante que sea honesto en sus respuestas. Puede usar el botón de ayuda (?) para obtener aclaraciones sobre cada pregunta. No hay respuestas correctas o incorrectas.\", \"professional\": \"El BDI-13 es un instrumento de screening y evaluación de severidad de síntomas depresivos. Administre en ambiente privado y cómodo. Evalúe inmediatamente cualquier respuesta positiva en el ítem de ideación suicida (ítem 7). La escala es sensible al cambio terapéutico y útil para monitoreo longitudinal. Considere factores contextuales y médicos que puedan influir en síntomas somáticos. Requiere integración con evaluación clínica completa para interpretación diagnóstica.\"}}, \"language\": \"es\", \"description\": \"Versión abreviada del inventario original de Beck desarrollada en 1974 para evaluación rápida y eficiente de la intensidad de los síntomas depresivos en adultos y adolescentes\", \"subcategory\": \"Evaluación de síntomas depresivos\", \"abbreviation\": \"BDI-13\", \"targetPopulation\": {\"ageGroups\": [\"adolescentes\", \"adultos\"], \"demographics\": \"Población general, estudiantes universitarios, pacientes clínicos\", \"clinicalConditions\": [\"Depresión mayor\", \"Trastornos del estado de ánimo\", \"Evaluación de severidad depresiva\"]}, \"administrationMode\": \"both\", \"estimatedDurationMinutes\": 5}, \"structure\": {\"sections\": [{\"id\": \"section-1\", \"items\": [{\"id\": \"item-1\", \"text\": \"Estado de ánimo\", \"number\": 1, \"metadata\": {\"helpText\": \"Evalúe su estado de ánimo general actual. Seleccione la opción que mejor refleje la intensidad de tristeza que experimenta.\", \"alertTrigger\": false}, \"required\": true, \"reversed\": false, \"responseType\": \"multiple_choice\", \"responseGroup\": \"bdi-13-scale\", \"specificOptions\": [{\"label\": \"No me encuentro triste\", \"score\": 0, \"value\": 0}, {\"label\": \"Me siento triste o melancólico\", \"score\": 1, \"value\": 1}, {\"label\": \"Constantemente estoy melancólico o triste y no puedo superarlo\", \"score\": 2, \"value\": 2}, {\"label\": \"Me siento tan triste o infeliz que no puedo resistirlo\", \"score\": 3, \"value\": 3}]}, {\"id\": \"item-2\", \"text\": \"Pesimismo\", \"number\": 2, \"metadata\": {\"helpText\": \"Considera sus expectativas sobre el futuro y si siente esperanza o desaliento respecto a lo que está por venir.\", \"alertTrigger\": false}, \"required\": true, \"reversed\": false, \"responseType\": \"multiple_choice\", \"responseGroup\": \"bdi-13-scale\", \"specificOptions\": [{\"label\": \"No soy particularmente pesimista y no me encuentro desalentado respecto al futuro\", \"score\": 0, \"value\": 0}, {\"label\": \"Me siento desanimado respecto al futuro\", \"score\": 1, \"value\": 1}, {\"label\": \"No tengo nada que esperar del futuro\", \"score\": 2, \"value\": 2}, {\"label\": \"No tengo ninguna esperanza en el futuro y creo que las cosas no pueden mejorar\", \"score\": 3, \"value\": 3}]}, {\"id\": \"item-3\", \"text\": \"Sentimiento de fracaso\", \"number\": 3, \"metadata\": {\"helpText\": \"Reflexione sobre cómo evalúa sus logros y experiencias de vida. Considere si se siente exitoso o fracasado en aspectos importantes.\", \"alertTrigger\": false}, \"required\": true, \"reversed\": false, \"responseType\": \"multiple_choice\", \"responseGroup\": \"bdi-13-scale\", \"specificOptions\": [{\"label\": \"No me siento fracasado\", \"score\": 0, \"value\": 0}, {\"label\": \"Creo que he fracasado más que la mayoría de las personas\", \"score\": 1, \"value\": 1}, {\"label\": \"Cuando miro hacia atrás en mi vida, todo lo que veo son un montón de fracasos\", \"score\": 2, \"value\": 2}, {\"label\": \"Creo que como persona soy un completo fracaso (como padre, marido, esposa etc)\", \"score\": 3, \"value\": 3}]}, {\"id\": \"item-4\", \"text\": \"Insatisfacción\", \"number\": 4, \"metadata\": {\"helpText\": \"Piense en su capacidad actual para disfrutar y sentirse satisfecho con actividades que normalmente le resultaban placenteras.\", \"alertTrigger\": false}, \"required\": true, \"reversed\": false, \"responseType\": \"multiple_choice\", \"responseGroup\": \"bdi-13-scale\", \"specificOptions\": [{\"label\": \"No estoy particularmente descontento\", \"score\": 0, \"value\": 0}, {\"label\": \"No disfruto de las cosas como antes\", \"score\": 1, \"value\": 1}, {\"label\": \"No encuentro satisfacción en nada\", \"score\": 2, \"value\": 2}, {\"label\": \"Me siento descontento de todo\", \"score\": 3, \"value\": 3}]}, {\"id\": \"item-5\", \"text\": \"Sentimientos de culpa\", \"number\": 5, \"metadata\": {\"helpText\": \"Evalúe si experimenta sentimientos de culpa excesiva, auto-reproches o sensación de ser una mala persona.\", \"alertTrigger\": false}, \"required\": true, \"reversed\": false, \"responseType\": \"multiple_choice\", \"responseGroup\": \"bdi-13-scale\", \"specificOptions\": [{\"label\": \"No me siento particularmente culpable\", \"score\": 0, \"value\": 0}, {\"label\": \"Me siento malo o indigno muchas veces\", \"score\": 1, \"value\": 1}, {\"label\": \"Me siento culpable\", \"score\": 2, \"value\": 2}, {\"label\": \"Pienso que soy muy malo e indigno\", \"score\": 3, \"value\": 3}]}, {\"id\": \"item-6\", \"text\": \"Autodesprecio\", \"number\": 6, \"metadata\": {\"helpText\": \"Considere sus sentimientos hacia usted mismo, incluyendo auto-aceptación, decepción o rechazo personal.\", \"alertTrigger\": false}, \"required\": true, \"reversed\": false, \"responseType\": \"multiple_choice\", \"responseGroup\": \"bdi-13-scale\", \"specificOptions\": [{\"label\": \"No me siento decepcionado conmigo mismo\", \"score\": 0, \"value\": 0}, {\"label\": \"Estoy decepcionado conmigo mismo\", \"score\": 1, \"value\": 1}, {\"label\": \"Estoy disgustado conmigo mismo\", \"score\": 2, \"value\": 2}, {\"label\": \"Me odio\", \"score\": 3, \"value\": 3}]}, {\"id\": \"item-7\", \"text\": \"Ideas suicidas\", \"number\": 7, \"metadata\": {\"helpText\": \"Esta pregunta evalúa pensamientos sobre hacerse daño o quitarse la vida. Si experimenta estos pensamientos, es importante que hable con un profesional inmediatamente.\", \"alertTrigger\": true}, \"required\": true, \"reversed\": false, \"responseType\": \"multiple_choice\", \"responseGroup\": \"bdi-13-scale\", \"specificOptions\": [{\"label\": \"No tengo pensamientos de dañarme\", \"score\": 0, \"value\": 0}, {\"label\": \"Creo que estaría mejor muerto\", \"score\": 1, \"value\": 1}, {\"label\": \"Tengo planes precisos para suicidarme\", \"score\": 2, \"value\": 2}, {\"label\": \"Me mataría si tuviera la oportunidad\", \"score\": 3, \"value\": 3}]}, {\"id\": \"item-8\", \"text\": \"Aislamiento social\", \"number\": 8, \"metadata\": {\"helpText\": \"Evalúe su interés actual en mantener relaciones sociales y conectar con otras personas comparado con su estado previo.\", \"alertTrigger\": false}, \"required\": true, \"reversed\": false, \"responseType\": \"multiple_choice\", \"responseGroup\": \"bdi-13-scale\", \"specificOptions\": [{\"label\": \"No he perdido el interés por los demás\", \"score\": 0, \"value\": 0}, {\"label\": \"Estoy menos interesado en los demás que antes\", \"score\": 1, \"value\": 1}, {\"label\": \"He perdido la mayor parte del interés por los demás y pienso poco en ellos\", \"score\": 2, \"value\": 2}, {\"label\": \"He perdido todo el interés por los demás y no me importa en absoluto\", \"score\": 3, \"value\": 3}]}, {\"id\": \"item-9\", \"text\": \"Indecisión\", \"number\": 9, \"metadata\": {\"helpText\": \"Piense en su capacidad actual para tomar decisiones, desde las más simples hasta las más complejas.\", \"alertTrigger\": false}, \"required\": true, \"reversed\": false, \"responseType\": \"multiple_choice\", \"responseGroup\": \"bdi-13-scale\", \"specificOptions\": [{\"label\": \"Tomo mis decisiones con la misma facilidad que antes\", \"score\": 0, \"value\": 0}, {\"label\": \"Trato de no tener que tomar decisiones\", \"score\": 1, \"value\": 1}, {\"label\": \"Tengo grandes dificultades para tomar decisiones\", \"score\": 2, \"value\": 2}, {\"label\": \"Ya no puedo tomar decisiones\", \"score\": 3, \"value\": 3}]}, {\"id\": \"item-10\", \"text\": \"Imagen corporal\", \"number\": 10, \"metadata\": {\"helpText\": \"Considere su percepción actual sobre su apariencia física y si ha notado cambios en cómo se ve a sí mismo.\", \"alertTrigger\": false}, \"required\": true, \"reversed\": false, \"responseType\": \"multiple_choice\", \"responseGroup\": \"bdi-13-scale\", \"specificOptions\": [{\"label\": \"No creo que mi aspecto haya empeorado\", \"score\": 0, \"value\": 0}, {\"label\": \"Estoy preocupado porque me veo viejo y poco atractivo\", \"score\": 1, \"value\": 1}, {\"label\": \"Creo que mi aspecto empeora constantemente quitándome atractivo\", \"score\": 2, \"value\": 2}, {\"label\": \"Siento que mi aspecto es feo y repulsivo\", \"score\": 3, \"value\": 3}]}, {\"id\": \"item-11\", \"text\": \"Capacidad laboral\", \"number\": 11, \"metadata\": {\"helpText\": \"Evalúe su capacidad actual para realizar actividades laborales, académicas o tareas cotidianas comparada con su funcionamiento previo.\", \"alertTrigger\": false}, \"required\": true, \"reversed\": false, \"responseType\": \"multiple_choice\", \"responseGroup\": \"bdi-13-scale\", \"specificOptions\": [{\"label\": \"Puedo trabajar igual de bien que antes\", \"score\": 0, \"value\": 0}, {\"label\": \"Me cuesta un esfuerzo especial comenzar a hacer algo\", \"score\": 1, \"value\": 1}, {\"label\": \"Debo esforzarme mucho para hacer cualquier cosa\", \"score\": 2, \"value\": 2}, {\"label\": \"No puedo realizar ningún trabajo\", \"score\": 3, \"value\": 3}]}, {\"id\": \"item-12\", \"text\": \"Fatiga\", \"number\": 12, \"metadata\": {\"helpText\": \"Considere su nivel de energía actual y si se siente más cansado o fatigado que de costumbre al realizar actividades.\", \"alertTrigger\": false}, \"required\": true, \"reversed\": false, \"responseType\": \"multiple_choice\", \"responseGroup\": \"bdi-13-scale\", \"specificOptions\": [{\"label\": \"No me canso más que antes\", \"score\": 0, \"value\": 0}, {\"label\": \"Me canso más fácilmente que antes\", \"score\": 1, \"value\": 1}, {\"label\": \"Me canso por cualquier cosa\", \"score\": 2, \"value\": 2}, {\"label\": \"Me canso demasiado por hacer cualquier cosa\", \"score\": 3, \"value\": 3}]}, {\"id\": \"item-13\", \"text\": \"Pérdida de apetito\", \"number\": 13, \"metadata\": {\"helpText\": \"Evalúe cambios en su apetito y patrones alimentarios comparados con su estado normal o previo.\", \"alertTrigger\": false}, \"required\": true, \"reversed\": false, \"responseType\": \"multiple_choice\", \"responseGroup\": \"bdi-13-scale\", \"specificOptions\": [{\"label\": \"Mi apetito es normal (igual que antes)\", \"score\": 0, \"value\": 0}, {\"label\": \"Mi apetito no es tan bueno como antes (subió o bajó)\", \"score\": 1, \"value\": 1}, {\"label\": \"Mi apetito es ahora mucho peor (bajó mucho o subió mucho)\", \"score\": 2, \"value\": 2}, {\"label\": \"He perdido el apetito por completo (no como nada o como cada que tengo oportunidad)\", \"score\": 3, \"value\": 3}]}], \"order\": 1, \"title\": \"Evaluación de Síntomas Depresivos\", \"description\": \"Síntomas cognitivos, afectivos y somáticos de depresión\"}], \"totalItems\": 13}, \"documentation\": {\"purpose\": \"Evaluación rápida y eficiente de la intensidad de síntomas depresivos. Útil para screening masivo, monitoreo de cambios terapéuticos y investigación epidemiológica en contextos donde el tiempo es limitado.\", \"version\": \"1.0\", \"lastUpdated\": \"2025-01-01\", \"bibliography\": [\"Beck, A.T., Rial, W.Y., & Rickels, K. (1974). Short form of Depression Inventory: Cross-validation. Psychological Reports, 34(3), 1184-1186.\", \"Conde, V., & Useros, E. (1974). Adaptación castellana de la escala de evaluación conductual para la depresión de Beck. Revista de Psiquiatría y Psicología Médica, 12(4), 217-236.\", \"Sanz, J., & Vázquez, C. (1998). Fiabilidad, validez y datos normativos del Inventario para la Depresión de Beck. Psicothema, 10(2), 303-318.\", \"Jurado, S., Villegas, M.E., Méndez, L., Rodríguez, F., Loperena, V., & Varela, R. (1998). La estandarización del Inventario de Depresión de Beck para los residentes de la Ciudad de México. Salud Mental, 21(3), 26-31.\", \"Beck, A.T., Ward, C.H., Mendelson, M., Mock, J., & Erbaugh, J. (1961). An inventory for measuring depression. Archives of General Psychiatry, 4(6), 561-571.\"], \"normativeData\": {\"sampleSize\": \"Múltiples estudios con más de 5,000 participantes acumulados\", \"cutoffPoints\": [{\"cutoff\": \"13\", \"condition\": \"Screening de depresión (sensibilidad 80%, especificidad 85%)\", \"population\": \"Población general mexicana\", \"evidenceLevel\": \"Evidencia nivel II - estudios de validación múltiples\"}, {\"cutoff\": \"12\", \"condition\": \"Detección de sintomatología depresiva significativa\", \"population\": \"Estudiantes universitarios\", \"evidenceLevel\": \"Evidencia nivel II - estudios específicos en población universitaria\"}, {\"cutoff\": \"15\", \"condition\": \"Screening en contexto médico general\", \"population\": \"Atención primaria\", \"evidenceLevel\": \"Evidencia nivel III - recomendación basada en práctica clínica\"}], \"demographics\": \"Población general adulta (18-65 años), estudiantes universitarios, poblaciones clínicas diversas en España, México y otros países latinoamericanos\", \"populationNorms\": {\"general\": \"Media: 4.2 ± 4.8 (rango: 0-25) en población general adulta\", \"clinical\": \"Media: 18.5 ± 8.4 (rango: 3-39) en poblaciones clínicas con depresión\", \"ageGroups\": \"Adolescentes: ligero incremento en medias debido a factores del desarrollo. Adultos mayores: considerar impacto de comorbilidades médicas\", \"genderDifferences\": \"Mujeres presentan medias ligeramente superiores (diferencia de 1-2 puntos) en estudios poblacionales\"}}, \"clinicalUtility\": \"Especialmente valorable en atención primaria, evaluaciones de seguimiento frecuentes, estudios poblacionales y cuando se requiere una medida breve pero válida de severidad depresiva. Ideal para monitoreo sesión a sesión en psicoterapia breve.\", \"clinicalValidation\": {\"clinicalTrials\": \"Utilizado en más de 500 estudios clínicos como medida de resultado primaria o secundaria\", \"treatmentOutcomes\": \"Correlación moderada-alta (r = 0.65-0.80) con mejora clínica global evaluada por clínicos\", \"diagnosticAccuracy\": \"Área bajo la curva ROC de 0.85-0.92 para detección de episodio depresivo mayor\"}, \"theoreticalFramework\": \"Basado en el modelo cognitivo de la depresión de Beck. Enfatiza la evaluación de la tríada cognitiva negativa (visión negativa de sí mismo, del mundo y del futuro) junto con síntomas afectivos y somáticos asociados.\", \"psychometricProperties\": {\"validity\": {\"construct\": \"Estructura bifactorial confirmada: Factor Cognitivo-Afectivo (9 ítems) y Factor Somático (4 ítems). Análisis factorial confirma modelo teórico propuesto.\", \"criterion\": \"Correlación alta con BDI completo (r = 0.96-0.98). Correlación moderada-alta con otras escalas de depresión (r = 0.70-0.85).\", \"sensitivity\": \"75-85% para detección de depresión mayor\", \"specificity\": \"85-95% para población general\", \"discriminant\": \"Correlaciones moderadas con escalas de ansiedad (r = 0.45-0.60), indicando especificidad adecuada para síntomas depresivos.\", \"negativePredicativeValue\": \"90-95% en poblaciones de baja prevalencia\", \"positivePredicativeValue\": \"70-80% dependiendo de prevalencia poblacional\"}, \"reliability\": {\"interRater\": \"0.95-0.98\", \"testRetest\": \"0.75-0.85 (1-4 semanas)\", \"cronbachAlpha\": \"0.85-0.92\"}, \"factorStructure\": \"Modelo bifactorial robusto: Factor 1 (Cognitivo-Afectivo): ítems 1-9, evalúa tríada cognitiva negativa y síntomas afectivos centrales; Factor 2 (Somático): ítems 10-13, evalúa manifestaciones físicas y neurovegetativas.\", \"minimumDetectableChange\": \"3-4 puntos en la escala total para cambio clínicamente significativo\"}}, \"interpretation\": {\"rules\": [{\"id\": \"rule-1\", \"color\": \"#22c55e\", \"label\": \"Depresión Mínima\", \"maxScore\": 8, \"minScore\": 0, \"severity\": \"minimal\", \"clinicalSignificance\": \"Sin sintomatología depresiva clínicamente significativa. No se evidencia presencia de criterios para episodio depresivo según clasificaciones diagnósticas actuales.\", \"clinicalInterpretation\": \"Las puntuaciones en este rango indican síntomas depresivos mínimos o ausentes. Los síntomas presentes no interfieren significativamente con el funcionamiento diario. El patrón de respuestas sugiere estado de ánimo dentro de parámetros normales.\", \"prognosticImplications\": \"Excelente pronóstico. Mantenimiento del funcionamiento normal esperado. Buena capacidad de afrontamiento ante estresores menores.\", \"differentialConsiderations\": \"Descartar trastornos de ansiedad aislados, trastornos adaptativos sin componente depresivo, o variaciones normales del estado de ánimo relacionadas con factores situacionales temporales.\", \"professionalRecommendations\": {\"immediate\": \"Continuar con estrategias de bienestar actual. No requiere intervención inmediata especializada.\", \"treatment\": \"Psicoeducación preventiva sobre factores protectores de salud mental. Mantener actividades placenteras y rutinas saludables.\", \"monitoring\": \"Monitoreo rutinario en seguimientos regulares de salud general. Re-evaluación si aparecen factores estresantes significativos.\", \"familySupport\": \"Informar a la familia sobre mantener ambiente de apoyo social. Promover comunicación abierta.\", \"riskAssessment\": \"Riesgo bajo para desarrollo de sintomatología depresiva. Evaluar factores de vulnerabilidad personal.\"}}, {\"id\": \"rule-2\", \"color\": \"#eab308\", \"label\": \"Depresión Leve\", \"maxScore\": 12, \"minScore\": 9, \"severity\": \"mild\", \"clinicalSignificance\": \"Sintomatología depresiva leve. Los síntomas están presentes pero no cumplen criterios completos para episodio depresivo mayor. Posible trastorno adaptativo con estado de ánimo deprimido o síntomas subsindrómicos de depresión.\", \"clinicalInterpretation\": \"Presencia de síntomas depresivos leves que pueden interferir ocasionalmente con el funcionamiento diario. Se evidencian cambios en el estado de ánimo y cognición, pero conserva capacidades de afrontamiento básicas.\", \"prognosticImplications\": \"Buen pronóstico con intervención temprana. Riesgo moderado de cronificación sin abordaje adecuado. Respuesta favorable esperada a intervenciones breves.\", \"differentialConsiderations\": \"Considerar trastorno adaptativo con síntomas depresivos, distimia de inicio reciente, síntomas depresivos secundarios a condiciones médicas, o reacciones normales de duelo no complicado.\", \"professionalRecommendations\": {\"immediate\": \"Evaluación de factores precipitantes y estresores actuales. Identificar recursos de apoyo disponibles.\", \"treatment\": \"Intervenciones psicoeducativas y estrategias de manejo del estrés. Considerar terapia cognitivo-conductual breve. Técnicas de activación conductual.\", \"monitoring\": \"Seguimiento quincenal durante primeras 6 semanas. Re-evaluación mensual posterior para monitorear evolución.\", \"familySupport\": \"Educación familiar sobre signos de progresión. Involucrar red de apoyo en estrategias de afrontamiento.\", \"riskAssessment\": \"Riesgo bajo-moderado para progresión a cuadro más severo. Monitorear factores estresantes adicionales.\"}}, {\"id\": \"rule-3\", \"color\": \"#f97316\", \"label\": \"Depresión Moderada\", \"maxScore\": 20, \"minScore\": 13, \"severity\": \"moderate\", \"clinicalSignificance\": \"Presencia de sintomatología depresiva moderada. Probable episodio depresivo mayor. Los síntomas interfieren significativamente con el funcionamiento social, laboral o en otras áreas importantes de la actividad.\", \"clinicalInterpretation\": \"Sintomatología depresiva moderada que interfiere de manera significativa con el funcionamiento diario. Se evidencian cambios considerables en cognición, afecto y conducta que requieren atención clínica estructurada.\", \"prognosticImplications\": \"Pronóstico favorable con tratamiento adecuado. Riesgo de recurrencia moderado. Importancia crucial de adherencia al tratamiento para prevenir cronicidad.\", \"differentialConsiderations\": \"Episodio depresivo mayor de intensidad moderada, trastorno bipolar en fase depresiva, trastorno depresivo persistente (distimia) con episodio superpuesto, o depresión secundaria a condiciones médicas generales.\", \"professionalRecommendations\": {\"immediate\": \"Intervención psicoterapéutica estructurada inmediata. Evaluación de necesidad de tratamiento farmacológico complementario.\", \"treatment\": \"Terapia cognitivo-conductual o interpersonal estructurada. Considerar farmacoterapia antidepresiva. Abordaje integral biopsicosocial.\", \"monitoring\": \"Seguimiento semanal las primeras 4-6 semanas. Evaluación de respuesta al tratamiento cada 2-3 semanas posteriormente.\", \"familySupport\": \"Educación familiar sobre naturaleza de la depresión. Involucrar activamente en plan de tratamiento. Capacitación en detección de señales de alarma.\", \"riskAssessment\": \"Evaluación sistemática de riesgo suicida en cada consulta. Establecer plan de seguridad. Monitorear ideación pasiva de muerte.\"}}, {\"id\": \"rule-4\", \"color\": \"#ef4444\", \"label\": \"Depresión Grave\", \"maxScore\": 39, \"minScore\": 21, \"severity\": \"severe\", \"clinicalSignificance\": \"Episodio depresivo mayor grave. Los síntomas son severos e interfieren marcadamente con el funcionamiento psicosocial. Alto riesgo de deterioro funcional progresivo y posibles complicaciones incluyendo ideación suicida activa.\", \"clinicalInterpretation\": \"Sintomatología depresiva grave que interfiere severamente con el funcionamiento diario. Se evidencia deterioro significativo en múltiples áreas de funcionamiento con posible presencia de síntomas psicóticos o riesgo suicida elevado.\", \"prognosticImplications\": \"Pronóstico reservado sin tratamiento intensivo inmediato. Alto riesgo de recurrencia y cronicidad. Posible deterioro funcional permanente sin intervención oportuna.\", \"differentialConsiderations\": \"Episodio depresivo mayor severo, episodio depresivo mayor con características psicóticas, episodio mixto en trastorno bipolar, o depresión mayor con características melancólicas o catatónicas.\", \"professionalRecommendations\": {\"immediate\": \"Intervención clínica inmediata. Evaluación prioritaria de riesgo suicida y necesidad de hospitalización. No dilatar inicio de tratamiento farmacológico.\", \"treatment\": \"Tratamiento farmacológico antidepresivo inmediato. Psicoterapia intensiva complementaria. Considerar técnicas de neuromodulación en casos refractarios.\", \"monitoring\": \"Seguimiento clínico intensivo bi-semanal mínimo. Contacto telefónico entre sesiones. Evaluación diaria en casos de riesgo suicida.\", \"familySupport\": \"Involucrar activamente a la familia en vigilancia y apoyo. Educación sobre signos de alarma. Establecer red de contención 24 horas.\", \"riskAssessment\": \"Evaluación prioritaria y continua de riesgo suicida. Considerar hospitalización si presenta ideación activa con plan o medios. Plan de seguridad detallado.\"}}], \"clinicalGuidelines\": {\"warningFlags\": [{\"message\": \"ALERTA CRÍTICA: Evaluación inmediata de riesgo suicida requerida. No dejar solo al paciente. Contactar con servicios de urgencia si es necesario.\", \"condition\": \"Puntuación ≥ 1 en ítem 7 (ideación suicida)\"}, {\"message\": \"ATENCIÓN: Deterioro rápido detectado. Re-evaluación clínica inmediata. Ajustar intensidad del tratamiento.\", \"condition\": \"Incremento súbito ≥ 5 puntos entre evaluaciones\"}, {\"message\": \"URGENCIA: Depresión severa identificada. Evaluación de necesidad de hospitalización. Tratamiento intensivo inmediato.\", \"condition\": \"Puntuación total ≥ 25\"}], \"contraindications\": [\"Deterioro cognitivo severo que impida comprensión\", \"Episodio psicótico activo con desorganización del pensamiento\", \"Estado confusional agudo o delirium\"], \"specialConsiderations\": [\"En adultos mayores, considerar impacto de comorbilidades médicas en síntomas somáticos\", \"En adolescentes, evaluar síntomas en contexto del desarrollo normal\", \"En pacientes con condiciones médicas crónicas, distinguir síntomas depresivos de manifestaciones físicas de la enfermedad\", \"Considerar diferencias culturales en expresión de síntomas afectivos y somáticos\"]}}, \"responseGroups\": {\"bdi-13-scale\": [{\"label\": \"Opción 0\", \"score\": 0, \"value\": 0}, {\"label\": \"Opción 1\", \"score\": 1, \"value\": 1}, {\"label\": \"Opción 2\", \"score\": 2, \"value\": 2}, {\"label\": \"Opción 3\", \"score\": 3, \"value\": 3}]}}','1.0',1,'2025-08-02 19:28:18.546','2025-08-02 19:28:18.546'),('bdi-21-1.0','{\"scoring\": {\"method\": \"sum\", \"subscales\": [{\"id\": \"cognitivos\", \"name\": \"Síntomas Cognitivos\", \"items\": [2, 3, 6, 8, 13], \"scoreRange\": {\"max\": 15, \"min\": 0}}, {\"id\": \"afectivos\", \"name\": \"Síntomas Afectivos\", \"items\": [1, 4, 5, 7, 9, 10, 11, 12], \"scoreRange\": {\"max\": 24, \"min\": 0}}, {\"id\": \"somaticos\", \"name\": \"Síntomas Somáticos\", \"items\": [14, 17, 20], \"scoreRange\": {\"max\": 9, \"min\": 0}}, {\"id\": \"vegetativos\", \"name\": \"Síntomas Vegetativos\", \"items\": [15, 16, 18, 19, 21], \"scoreRange\": {\"max\": 15, \"min\": 0}}], \"scoreRange\": {\"max\": 63, \"min\": 0}, \"reversedItems\": [], \"specialScoring\": {\"notes\": \"Sistema de puntuación variable por ítem. Cada ítem tiene entre 4-8 opciones con puntuaciones específicas de 0-3. El ítem 16 (sueño) tiene análisis cualitativo específico por patrón.\"}}, \"metadata\": {\"id\": \"bdi-21-1.0\", \"name\": \"Inventario de Depresión de Beck (Versión Original)\", \"year\": \"1961\", \"authors\": [\"Aaron T. Beck\", \"C. H. Ward\", \"M. Mendelson\", \"J. Mock\", \"J. Erbaugh\"], \"version\": \"1.0\", \"category\": \"Depresión\", \"helpText\": {\"general\": \"El BDI-21 es el inventario original de Beck que proporciona una evaluación integral de síntomas depresivos. Evalúa 21 dominios específicos incluyendo síntomas cognitivos, afectivos, somáticos y vegetativos. Es especialmente útil para evaluación diagnóstica detallada y planificación terapéutica específica según perfiles sintomáticos.\", \"instructions\": {\"patient\": \"Este cuestionario contiene 21 grupos de afirmaciones sobre diferentes aspectos de cómo puede sentirse una persona. Lea íntegro el grupo de afirmaciones de cada apartado y escoja la afirmación que mejor describa el modo en que se siente en el tiempo más reciente. Es importante que lea todas las opciones antes de seleccionar. Sea honesto en sus respuestas ya que esto ayudará a su profesional a entender mejor su situación. Puede usar el botón de ayuda (?) si necesita aclaración sobre alguna pregunta.\", \"professional\": \"El BDI-21 ofrece una evaluación integral de síntomas depresivos mediante 21 dominios específicos. Administre en ambiente privado garantizando comprensión de instrucciones. Evalúe inmediatamente cualquier respuesta positiva en ítem de ideación suicida (ítem 9). El análisis por subescalas permite identificar perfiles sintomáticos específicos para personalizar intervenciones terapéuticas. La escala es altamente sensible al cambio y útil para monitoreo detallado de evolución. Considere que algunos ítems tienen sistemas de puntuación específicos.\"}}, \"language\": \"es\", \"description\": \"Instrumento original desarrollado por Beck en 1961 para evaluación integral y detallada de la intensidad de síntomas depresivos en adultos y adolescentes, considerando múltiples dominios sintomáticos\", \"subcategory\": \"Evaluación integral de síntomas depresivos\", \"abbreviation\": \"BDI-21\", \"targetPopulation\": {\"ageGroups\": [\"adolescentes\", \"adultos\"], \"demographics\": \"Población general, estudiantes universitarios, pacientes clínicos, población médica\", \"clinicalConditions\": [\"Depresión mayor\", \"Trastornos del estado de ánimo\", \"Trastorno bipolar\", \"Evaluación de severidad depresiva\", \"Monitoreo terapéutico\"]}, \"administrationMode\": \"both\", \"estimatedDurationMinutes\": 15}, \"structure\": {\"sections\": [{\"id\": \"section-1\", \"items\": [{\"id\": \"item-1\", \"text\": \"Estado de ánimo\", \"number\": 1, \"metadata\": {\"helpText\": \"Evalúe su estado de ánimo actual, específicamente sentimientos de tristeza o depresión. Considere la intensidad y persistencia de estos sentimientos.\", \"alertTrigger\": false}, \"required\": true, \"reversed\": false, \"responseType\": \"multiple_choice\", \"specificOptions\": [{\"label\": \"NO me encuentro triste\", \"score\": 0, \"value\": 0}, {\"label\": \"Me siento algo triste y deprimido\", \"score\": 1, \"value\": 1}, {\"label\": \"Esta tristeza me produce verdaderos sufrimientos\", \"score\": 2, \"value\": 2}, {\"label\": \"Tengo SIEMPRE como una pena encima que no me la puedo quitar\", \"score\": 2, \"value\": 3}, {\"label\": \"Ya no puedo soportar esta pena\", \"score\": 3, \"value\": 4}]}, {\"id\": \"item-2\", \"text\": \"Pesimismo\", \"number\": 2, \"metadata\": {\"helpText\": \"Considere su perspectiva sobre el futuro, sus expectativas y el grado de esperanza o desesperanza que experimenta.\", \"alertTrigger\": false}, \"required\": true, \"reversed\": false, \"responseType\": \"multiple_choice\", \"specificOptions\": [{\"label\": \"Me siento desanimado cuando pienso en el futuro\", \"score\": 1, \"value\": 0}, {\"label\": \"Creo que nunca me recuperaré de mis penas\", \"score\": 2, \"value\": 1}, {\"label\": \"No soy especialmente pesimista, ni creo que las cosas me vayan a ir mal\", \"score\": 0, \"value\": 2}, {\"label\": \"NO espero nada bueno de la vida\", \"score\": 2, \"value\": 3}, {\"label\": \"No espero nada. Esto NO tiene remedio\", \"score\": 3, \"value\": 4}]}, {\"id\": \"item-3\", \"text\": \"Sentimiento de fracaso\", \"number\": 3, \"metadata\": {\"helpText\": \"Reflexione sobre cómo evalúa sus logros y éxitos en diferentes áreas de su vida (personal, laboral, familiar, etc.).\", \"alertTrigger\": false}, \"required\": true, \"reversed\": false, \"responseType\": \"multiple_choice\", \"specificOptions\": [{\"label\": \"He fracasado TOTALMENTE como persona (padre, madre, marido, hijo, profesional, etc.)\", \"score\": 3, \"value\": 0}, {\"label\": \"He tenido MÁS fracasos que